var cycle = module.exports;

cycle.beforeStartup = function(app, next) {
    app.world.beforeStartup(next);
};

cycle.afterStartup = function(app, next) {
    app.world.afterStartup(next);
};

cycle.beforeShutdown = function(app, next) {
    app.world.beforeShutdown(next);
};

cycle.afterStartAll = function(app) {
    app.world.afterStartAll();
};